from xbmctorrent.plugin import plugin

@plugin.route("/torrentplay/<url>/<index>/<name>")
def torrent_play(url, index, name = ""):
    from xbmctorrent.acestream import AceStreamPlayer
    AceStreamPlayer().init(url, index, name).loop()

@plugin.route("/play_torrent_raw/<raw>/<index>/<name>")
def play_torrent_raw(raw, index, name = ""):
    from xbmctorrent.acestream import AceStreamPlayer
    AceStreamPlayer().init(raw, index, name, True).loop()
